export default {
  data: function data() {
    return {
      isLoading: false,
      code: null,
      isClick: false
    };
  },
  created: function created() {
    var self = this;
    setTimeout(function () {
      self.isLoading = true;
    }, 500);
  },
  methods: {
    shipBlur: function shipBlur() {
      this.setInputShow();
    },
    shipFocus: function shipFocus() {
      this.setInputShow();
    },
    changeInput: function changeInput() {
      this.setInputShow();
    },
    setInputShow: function setInputShow() {
      if (this.code != null && this.code != '') {
        this.isClick = true;
      } else {
        this.isClick = false;
      }
    },
    cardBtn: function cardBtn() {
      var _this = this;

      if (!this.isClick) {
        this.$toast('请输入激活码');
        return;
      }

      this.$toast.loading({
        message: '正在激活...',
        forbidClick: true
      });
      this.$axios.setVipClubCard(this.code).then(function (res) {
        if (!res.data.success) {
          _this.$toast(res.data.info);
        } else {
          _this.$toast.success('激活成功');
        }
      })["catch"](function (err) {});
    }
  },
  components: {}
};
