export default {
  data: function data() {
    return {
      isLoading: false,
      audioList: [],
      loading: false,
      finished: false,
      page: 1,
      total: 0
    };
  },
  components: {},
  created: function created() {
    var self = this; // setTimeout(res => {

    this.isLoading = true; // }, 1000);
  },
  methods: {
    onLoad: function onLoad() {
      var _this = this;

      // 异步更新数据
      // setTimeout 仅做示例，真实场景中一般为 ajax 请求
      setTimeout(function () {
        _this.$axios.getVipContent(_this.page).then(function (res) {
          _this.page += 1;
          _this.audioList = _this.audioList.concat(res.data.data.list);
          _this.total = res.data.data.totalPage;
        })["catch"](function (err) {}); // 加载状态结束


        _this.loading = false; // 数据全部加载完成

        console.log(_this.total);
        console.log(_this.page);

        if (_this.page >= _this.total) {
          _this.finished = true;
        }
      }, 1000);
    },
    musicDaile: function musicDaile(id, name) {
      try {
        var data = {
          type: 1,
          id: id,
          name: name
        };

        if (this.system == 'ios') {
          window.webkit.messageHandlers.share.postMessage(data);
        } else if (this.system == 'android') {
          console.log('专辑详情');
          window.android.postMessage('share', JSON.stringify(data));
        }
      } catch (e) {
        this.$toast('请在APP中打开'); //TODO handle the exception
      }
    }
  }
};
