export default {
  props: {},
  data: function data() {
    return {
      today: new Date(),
      year: '',
      month: '',
      day: '',
      date: '',
      dateArr: [],
      checkin: [],
      ShowTime: 1,
      currentMouth: null,
      defaultIndex: -1,
      isUnfold: false,
      isMonth: false
    };
  },
  created: function created() {
    this.year = this.today.getFullYear();
    this.month = this.today.getMonth() + 1;
    this.currentMouth = this.today.getMonth() + 1;
    this.day = this.today.getDay();
    this.date = this.today.getDate();
    console.log('date===', this.date);
    this.getCalendar();
    this.getBabyDay(this.month);
  },
  methods: {
    showClick: function showClick() {
      this.isUnfold = !this.isUnfold;
    },
    getBabyDay: function getBabyDay() {
      var self = this;
      self.checkin = [];
      this.$axios.getBabyClock(this.month).then(function (res) {
        if (res.data.code == 1) {
          var list = res.data.data.calendar;
          list.forEach(function (item, index) {
            if (item.is_clocked == '是') {
              var time = self.timestampToTime(item.calc_date);
              console.log(time);
              self.checkin.push(time);
            }
          });
        } else {
          self.checkin = [];
        }
      })["catch"](function (err) {});
    },
    timestampToTime: function timestampToTime(cjsj) {
      var date = new Date(cjsj); //时间戳为10位需*1000，时间戳为13位的话不需乘1000

      var Y = date.getFullYear() + '-';
      var M = (date.getMonth() + 1 < 10 ? '0' + (date.getMonth() + 1) : date.getMonth() + 1) + '-';
      var D = date.getDate() + ' ';
      var h = date.getHours() + ':';
      var m = date.getMinutes() + ':';
      var s = date.getSeconds();
      return D;
    },
    isLeap: function isLeap() {
      var year = this.year;

      if (year % 4 == 0 && year % 100 > 0) {
        return true;
      } else if (year % 400 == 0 && year % 3200 > 0) {
        return true;
      } else {
        return false;
      }
    },
    getLen: function getLen() {
      var month = this.month;

      if (month == 2) {
        if (this.isLeap) {
          return 29;
        } else {
          return 28;
        }
      } else {
        if (month < 8) {
          if (month % 2 > 0) {
            return 31;
          } else {
            return 30;
          }
        } else {
          if (month % 2 > 0) {
            return 30;
          } else {
            return 31;
          }
        }
      }
    },
    getCalendarTime: function getCalendarTime() {
      return this.year + '-' + this.month + '-' + this.date;
    },
    getCalendar: function getCalendar() {
      var len = this.getLen();
      var d = new Date(this.year, this.month - 1, 1);
      var dfw = d.getDay();
      var arr = new Array();
      var tem = 0;

      for (var i = 0; i < 6; i++) {
        arr[i] = new Array();

        for (var j = 0; j < 7; j++) {
          tem++;

          if (tem - dfw > 0 && tem - dfw <= len) {
            arr[i][j] = tem - dfw;

            if (this.date == tem - dfw && this.month == this.currentMouth) {
              this.defaultIndex = i;
              this.isMonth = true;
            }
          } else {
            arr[i][j] = '';
          }
        }
      }

      this.dateArr = arr; // console.log(arr);
    },
    nextMonth: function nextMonth() {
      if (this.month == 12) {
        this.year++;
        this.month = 1;
      } else {
        this.month++;
      }

      this.defaultIndex = 0;
      this.isMonth = false;
      this.getCalendar();
      this.getBabyDay();
    },
    prevMonth: function prevMonth() {
      if (this.month == 1) {
        this.year--;
        this.month = 12;
      } else {
        this.month--;
      }

      this.defaultIndex = 0;
      this.isMonth = false;
      this.getBabyDay();
      this.getCalendar();
    },
    contains: function contains(arr, index) {
      if (!this.isUnfold) {
        if (index != this.defaultIndex) {
          return;
        } else {
          if (arr[0] == '' && arr[1] == '' && arr[2] == '' && arr[3] == '' && arr[4] == '' && arr[5] == '' && arr[6] == '') {
            return false;
          } else {
            return true;
          }
        }
      } else {
        if (arr[0] == '' && arr[1] == '' && arr[2] == '' && arr[3] == '' && arr[4] == '' && arr[5] == '' && arr[6] == '') {
          return false;
        } else {
          return true;
        }
      }
    },
    isCheck: function isCheck(index) {
      for (var i in this.checkin) {
        if (index == this.checkin[i]) {
          return true;
        }
      }

      return false;
    }
  }
};
