export function fetch(options) {
  return new Promise(function (resolve, reject) {
    var instance = axios.create({
      //instance创建一个axios实例，可以自定义配置，可在 axios文档中查看详情
      //所有的请求都会带上这些配置，比如全局都要用的身份信息等。
      headers: {
        "Content-Type": "application/json" // 'token':"eyJ0eXBlIjoiSldUIiwiYWxnIjoiSFMyNTYifQ.eyJleHAiOjE1ODc0OTA5NTcsIm5iZiI6MTU4Mzg5MDk1N30.4uO3puLj52psZgbfTQgNPkeqzk_pn9-M2-eziyx1WhU",//token从全局变量那里传过来

      },
      timeout: 30 * 1000 // 30秒超时

    });
    instance.defaults.headers.common["token"] = AUTH_TOKEN;
    instance.interceptors.request.use(function (config) {
      // console.log("AUTH_TOKEN")
      if (AUTH_TOKEN == null) {
        var promisefresh = new Promise(function (resolve, reject) {
          //刷新token
          axios.get("http://api.cloud.alilo.com.cn/api/v4/login/get-token?appKey=hht&appSecret=hht").then(function (response) {
            config.headers.token = response.data.data;
            localStorage.setItem('token', response.data.data);
            resolve(config);
          });
        });
        return promisefresh;
      }

      var url = config.url;

      if (url.indexOf("login") > -1) {
        localStorage.setItem('token', "");
        config.headers.token = "";
      }

      if (url.indexOf("user") > -1 && url.indexOf("login") < 0) {
        config.headers.token = localStorage.getItem("token");
      }

      return config;
    }, function (err) {
      return Promise.reject(err);
    });
    instance.interceptors.response.use(function (res) {
      // console.log(res)
      if (res.data.code == 2) {
        // console.log("token过期了", Router);
        Router.push({
          name: 'login'
        });
      }

      return res;
    }, function (err) {
      return err;
    });
    instance(options).then(function (response) {
      //then 请求成功之后进行什么操作
      resolve(response); //把请求到的数据发到引用请求的地方
    })["catch"](function (error) {
      // console.log("请求异常信息：" + error);
      reject(error);
    });
  });
}
