import { mapActions, mapMutations, mapState, mapGetters } from 'vuex';
export default {
	data() {
		return {
			isLoading: false,
			activityList: [],
			helpList: [],
			audioList: [],
			cardList: [],
			cardIndex: 0,
			buyLink: null,
			buyArray: [],
			buyOneArray: [],
			activeName: '0',
			message: 'dilidilibao'
		};
	},
	computed: {
		...mapState(['equityList', 'system', 'babyInfo', 'memberInfo', 'userInfo']),
		activeHelp: function() {
			return this.helpList.filter(function(item, index) {
				return index < 4;
			});
		},
		activeActivityList: function() {
			var idx = 0;
			return this.activityList.filter(function(item, index, array) {
				if (item.activityStateStr == '进行中') {
					idx += 1;
					if (idx < 3) {
						return item;
					}
				}
			});
		}
	},
	created() {
		this.getVipAll();
		console.log('11111', this.buyOneArray);
	},
	mounted() {},
	methods: {
		// ...mapActions(['setUserInfoAction', 'setEquityAction']),
		cardClick(index, url, data) {
			this.cardIndex = index;
			this.buyLink = url;
			this.buyArray = data;
			this.buyOneArray = data;
			this.$store.dispatch('setEquityAction', this.buyOneArray.id);
		},
		//客服
		serviceBox() {
			// if (this.memberInfo.code == 0) {
			// 	this.$toast('请先开通会员');
			// 	return;
			// }
			this.$dialog
				.alert({
					title: '客服',
					message: '添加客服微信：dilidilibao'
				})
				.then(() => {
					// on close
					this.$copyText(this.message).then(
						res => {
							this.$toast('微信号复制成功!');
						},
						err => {
							alert('Can not copy');
						}
					);
				});
		},
		activityRouter(url) {
			location.href = url;
		},
		setPhone(tel) {
			try {
				return tel.replace(tel.substring(3, 7), '****');
			} catch (e) {
				return '火火兔用户';
				//TODO handle the exception
			}
		},
		loca() {
			location.reload();
		},
		cardPayBtn() {
			console.log('调用支付===========', this.babyInfo);
			if (this.buyLink == null) {
				this.$toast('请选择要开通的会员卡');
				return;
			}
			this.$router.push({ name: 'purchase-help', query: { url: this.buyLink } });
		},
		setRouter(val, flag) {
			if (this.memberInfo.code == 0 && !flag) {
				this.$toast('请先开通会员');
				return;
			}
			this.$router.push({ name: val });
		},
		helpClick(index) {
			this.$router.push({ name: 'member-equity', query: { id: index } });
		},
		getVipAll() {
			console.log('调用接口');
			this.$axios
				.getVipContent(1)
				.then(res => {
					this.audioList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.getVipActivity()
				.then(res => {
					this.activityList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.getUserHelp()
				.then(res => {
					this.helpList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.getMemberCard()
				.then(res => {
					this.cardList = res.data.data.list;
					this.buyLink = this.cardList[0].buyLink;
					this.buyOneArray = this.cardList[0];
					this.$store.dispatch('setEquityAction', this.buyOneArray.id);
				})
				.catch(err => {});
			setTimeout(res => {
				this.isLoading = true;
			}, 300);
		},
		//专属内容
		musicDaile(id, name) {
			try {
				let data = {
					type: 1,
					id: id,
					name: name
				};
				if (this.system == 'ios') {
					window.webkit.messageHandlers.share.postMessage(data);
				} else if (this.system == 'android') {
					console.log('专辑详情');
					window.android.postMessage('share', JSON.stringify(data));
				}
			} catch (e) {
				this.$toast('请更新新版火火兔APP');
				//TODO handle the exception
			}
		},
		ToText(HTML) {
			var input = HTML;
			return input
				.replace(/<(style|script|iframe)[^>]*?>[\s\S]+?<\/\1\s*>/gi, '')
				.replace(/<[^>]+?>/g, '')
				.replace(/\s+/g, ' ')
				.replace(/ /g, ' ')
				.replace(/>/g, ' ');
		}
	},
	components: {},
	watch: {
		a(val, oldVal) {
			//普通的watch监听
			console.log('a:===========' + val, oldVal);
		}
	}
};
