export default {
	data() {
		return {
			topList: [],
			currentTime: null,
			currentMouth: null,
			updateTime: 0,
			userid: null,
			userList: [],
			userScore: 0
		};
	},
	created() {
		this.current();
		this.getBabyRanking();
	},
	methods: {
		getBabyRanking() {
			let self = this;
			this.$axios
				.getBabyRanking(this.currentTime)
				.then(res => {
					this.topList = res.data.data.list;
					let babyArray = res.data.data.list;
					this.updateTime = res.data.data.updatetime;
					this.userid = localStorage.getItem('user');
					babyArray.forEach((value, index) => {
						if (value.userId == self.userid) {
							console.log(index);
							self.userList = value;
							console.log('self.userList', self.userList);
							self.userScore = index + 1;
						}
					});
				})
				.catch(err => {});
		},
		current() {
			var dd = new Date();
			var y = dd.getFullYear();
			var m = dd.getMonth() + 1; //获取当前月份的日期
			var d = dd.getDate();
			this.currentTime = y + '-' + m + '-' + d;
			this.currentMouth = m;
		}
	},
	components: {}
};
