export default {
	data() {
		return {
			isLoading: false,
			code: null,
			isClick: false
		};
	},
	created() {
		let self = this;
		setTimeout(function() {
			self.isLoading = true;
		}, 500);
	},
	methods: {
		shipBlur() {
			this.setInputShow();
		},
		shipFocus() {
			this.setInputShow();
		},
		changeInput() {
			this.setInputShow();
		},
		setInputShow() {
			if (this.code != null && this.code != '') {
				this.isClick = true;
			} else {
				this.isClick = false;
			}
		},
		cardBtn() {
			if (!this.isClick) {
				this.$toast('请输入激活码');
				return;
			}
			this.$toast.loading({
			  message: '正在激活...',
			  forbidClick: true
			});
			this.$axios
				.setVipClubCard(this.code)
				.then(res => {
					if(!res.data.success){
						this.$toast(res.data.info);
					}else {
						this.$toast.success('激活成功');
					}
				})
				.catch(err => {});
		}
	},
	components: {}
};
