import Title from '@/components/Title.vue';
import Data from '@/components/Data.vue';
import Pie from '@/components/Pie.vue';
import Suspend from '@/components/Suspend.vue';
import BabaList from '@/components/BabyList.vue';
import { mapActions, mapMutations, mapState, mapGetters } from 'vuex';
export default {
	data() {
		return {
			isLoading: false,
			title: ['今日学习关键词', '今日强化重点', '宝宝关键期'],
			age: '0个月',
			periodList: [],
			currentTime: null,
			currentMouth: null,
			sumDay: 0,
			keyList: [],
			keyArray: [],
			isPie: false,
			applyTime: 0,
			babyYear: 0,
			babyMonth: 0,
			isApply: false
		};
	},
	computed: {
		...mapState(['system', 'babyInfo', 'memberInfo'])
	},
	created() {
		this.current();
		this.getSumTime();
		console.log('babyInfo', this.babyInfo);
	},
	methods: {
		goReport() {
			this.$router.push({ name: 'report' });
		},
		loca() {
			location.reload();
		},
		setRouter(val) {
			this.$router.push({ name: val });
		},
		countAge(year, month) {
			var sum = year * 12 + month;
			console.log('nianling,', sum);
			if (sum < 13) {
				return month + '-' + (month + 1) + '个月';
			} else if (sum >= 13 && sum < 18) {
				return '1-1.5岁';
			} else if (sum >= 18 && sum < 24) {
				return '1.5-2岁';
			} else if (sum >= 24 && sum < 36) {
				return '2-3岁';
			} else if (sum >= 36 && sum < 48) {
				return '3-4岁';
			} else if (sum >= 48 && sum < 60) {
				return '4-5岁';
			} else if (sum >= 60 && sum < 72) {
				return '5-6岁';
			}
		},
		//计算宝宝多少天
		getBirthSlot(date) {
			var time;
			try {
				time = date
					.replace('年', '-')
					.replace('月', '-')
					.replace('日', '');
			} catch (e) {
				//TODO handle the exception
				time = this.currentTime;
			}
			let birthDay = new Date(time);
			let nowDate = new Date();
			let date1 = Date.parse(birthDay);
			let date2 = Date.parse(nowDate);
			let day = Math.floor((date2 - date1) / (60 * 60 * 1000 * 24));
			let age = '';
			let year = Math.floor(day / 365);
			let y = day % 365;
			let month = Math.floor(y / 30);
			let d = Math.floor((day % 365) % 30);
			age += year + '岁' + month + '月' + d + '天';
			this.babyYear = year;
			this.babyMonth = month;
			return age;
		},
		getUserDayCourse() {
			let self = this;
			this.$axios
				.getDayKey(this.currentTime)
				.then(res => {
					let pieData = res.data.data.emphasis;
					pieData.forEach(function(Data, index) {
						let v = Data.rate.replace('%', '');
						let obj = {
							name: Data.ability + Data.rate,
							value: v,
							size: v
						};
						self.keyList.push(obj);
					});
					console.log('self.keyList', self.keyList);
					let arr = res.data.data.key;
					self.keyArray = arr.split('、');
					self.isPie = true;
				})
				.catch(err => {});
			this.$axios
				.getBabyPeriod(this.currentTime)
				.then(res => {
					this.periodList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.sumTime(this.currentMouth)
				.then(res => {
					this.sumDay = res.data.data.clockDays;
				})
				.catch(err => {});
		},
		getSumTime() {
			this.$axios
				.userApplyTime()
				.then(res => {
					this.applyTime = this.computedTime(res.data.data);
					this.isLoading = true;
					let time = res.data.data;
					if (time.indexOf('0') != -1) {
						this.getUserDayCourse();
						this.isApply = true;
					}
				})
				.catch(err => {});
		},
		userApply() {
			if (this.babyInfo.length == 0) {
				this.$toast('请先在APP中添加宝宝信息');
				return;
			}
			if (this.memberInfo.code == 0) {
				this.$router.push({ name: 'index' });
				this.$toast('请先开通会员');
				return;
			}
			this.$toast.loading({
				message: '报名中...',
				forbidClick: true
			});
			this.$axios
				.userApply()
				.then(res => {
					if (res.data.code == 1) {
						this.$toast('报名成功');
						this.getUserDayCourse();
						this.isApply = true;
						return true;
					}
				})
				.catch(err => {});
		},
		computedTime(old) {
			//传入之前的时间  时间格式为(YY-MM-DD HH:MM:SS)
			console.log(old);
			var returnText = '';
			var nowDate = new Date().getTime(); //当前时间
			var setDate = new Date(old).getTime();
			console.log('setDate', setDate);
			var times = Math.floor((nowDate - setDate) / 1000);
			console.log('times', times / (60 * 60 * 24));
			returnText = Math.ceil(times / (60 * 60 * 24));
			console.log('returnText11====', returnText);
			return returnText;
		},
		current() {
			var dd = new Date();
			var y = dd.getFullYear();
			var m = dd.getMonth() + 1; //获取当前月份的日期
			var d = dd.getDate();
			this.currentTime = y + '-' + m + '-' + d;
			this.currentMouth = m;
		},
		education() {
			this.$axios
				.getDayCourse(this.currentTime)
				.then(res => {
					console.log('点击====');
					// let array = { title: '智慧早教第' + this.applyTime + '天', course: [] };
					let array = [];
					if (res.data.code == 1) {
						let item = res.data.data;
						item.forEach(function(data, index) {
							let obj = {
								url: data.url,
								id: data.id,
								name: data.name
							};
							// array.course.push(obj);
							array.push(obj);
						});
						console.log('array', array);
						try {
							this.$toast('获取早教课程成功');
							if (this.system == 'ios') {
								window.webkit.messageHandlers.course_play.postMessage(array);
							} else if (this.system == 'android') {
								console.log('安卓============', JSON.stringify(array));
								window.android.playCourse('course_play', JSON.stringify(array));
							}
						} catch (e) {
							this.$toast('获取早教课程失败');
							//TODO handle the exception
							console.log('请在app中打开11111');
						}
					} else {
						this.$toast('暂无今日早教课程');
					}
				})
				.catch(err => {});
		}
	},
	components: {
		'v-data': Data,
		'v-title': Title,
		'v-pie': Pie,
		'v-suspend': Suspend,
		'v-babaList': BabaList
	}
};
