export default {
	data() {
		return {
			isLoading: false,
			audioList: [],
			loading: false,
			finished: false,
			page: 1,
			total: 0
		};
	},
	components: {},
	created() {
		let self = this;
		// setTimeout(res => {
		this.isLoading = true;
		// }, 1000);
	},
	methods: {
		onLoad() {
			// 异步更新数据
			// setTimeout 仅做示例，真实场景中一般为 ajax 请求
			setTimeout(() => {
				this.$axios
					.getVipContent(this.page)
					.then(res => {
						this.page += 1;
						this.audioList = this.audioList.concat(res.data.data.list);
						this.total = res.data.data.totalPage;
					})
					.catch(err => {});
				// 加载状态结束
				this.loading = false;

				// 数据全部加载完成
				console.log(this.total)
				console.log(this.page)
				if (this.page >= this.total) {
					this.finished = true;
				}
			}, 1000);
		},
		musicDaile(id, name) {
			try {
				let data = {
					type: 1,
					id: id,
					name: name
				};
				if (this.system == 'ios') {
					window.webkit.messageHandlers.share.postMessage(data);
				} else if (this.system == 'android') {
					console.log('专辑详情');
					window.android.postMessage('share', JSON.stringify(data));
				}
			} catch (e) {
				this.$toast('请在APP中打开');
				//TODO handle the exception
			}
		}
	}
};
