export default {
	props: {},
	data() {
		return {
			today: new Date(),
			year: '',
			month: '',
			day: '',
			date: '',
			dateArr: [],
			checkin: [],
			currentMouth: null
		};
	},
	created() {
		this.year = this.today.getFullYear();
		this.month = this.today.getMonth() + 1;
		this.currentMouth = this.today.getMonth() + 1;
		this.day = this.today.getDay();
		this.date = this.today.getDate();
		this.getCalendar();
		this.getBabyDay(this.month);
	},
	methods: {
		getBabyDay() {
			let self = this;
			self.checkin =[];
			this.$axios
				.getBabyClock(this.month)
				.then(res => {
					if (res.data.code == 1) {
						let list = res.data.data.calendar;
						list.forEach(function(item, index) {
							if (item.is_clocked == 'æ˜¯') {
								let time = item.calc_date.split('-');
								self.checkin.push(time[2]);
							}
						});
					}else {
						self.checkin =[];
					}
				})
				.catch(err => {});
		},
		isLeap() {
			const year = this.year;
			if (year % 4 == 0 && year % 100 > 0) {
				return true;
			} else if (year % 400 == 0 && year % 3200 > 0) {
				return true;
			} else {
				return false;
			}
		},
		getLen() {
			const month = this.month;
			if (month == 2) {
				if (this.isLeap) {
					return 29;
				} else {
					return 28;
				}
			} else {
				if (month < 8) {
					if (month % 2 > 0) {
						return 31;
					} else {
						return 30;
					}
				} else {
					if (month % 2 > 0) {
						return 30;
					} else {
						return 31;
					}
				}
			}
		},
		getCalendarTime() {
			return this.year + '-' + this.month + '-' + this.date;
		},
		getCalendar() {
			var len = this.getLen();
			var d = new Date(this.year, this.month - 1, 1);
			var dfw = d.getDay();
			var arr = new Array();
			var tem = 0;
			for (var i = 0; i < 6; i++) {
				arr[i] = new Array();
				for (var j = 0; j < 7; j++) {
					tem++;
					if (tem - dfw > 0 && tem - dfw <= len) {
						arr[i][j] = tem - dfw;
					} else {
						arr[i][j] = '';
					}
				}
			}
			this.dateArr = arr;
			// console.log(arr);
		},
		nextMonth() {
			if (this.month == 12) {
				this.year++;
				this.month = 1;
			} else {
				this.month++;
			}
			this.getCalendar();
		},
		prevMonth() {
			if (this.month == 1) {
				this.year--;
				this.month = 12;
			} else {
				this.month--;
			}
			this.getBabyDay();
			this.getCalendar();
		},
		contains(arr) {
			// console.log('arr', arr);
			if (arr[0] == '' && arr[1] == '' && arr[2] == '' && arr[3] == '' && arr[4] == '' && arr[5] == '' && arr[6] == '') {
				return false;
			} else {
				return true;
			}
		},
		isCheck(index) {
			for (let i in this.checkin) {
				if (index == this.checkin[i]) {
					return true;
				}
			}
			return false;
		}
	}
};
