import { mapActions, mapMutations, mapState, mapGetters } from 'vuex';
export default {
	data() {
		return {
			isLoading: false,
			activityList: [],
			helpList: [],
			audioList: [],
			cardList: [],
			cardIndex: -1,
			buyLink: null
		};
	},
	computed: {
		...mapState(['equityList', 'system', 'babyInfo', 'memberInfo'])
	},
	created() {
		this.$store.dispatch('setEquityAction');
		this.getVipAll();
		console.log(this.memberInfo);
	},
	mounted() {},
	methods: {
		// ...mapActions(['setUserInfoAction', 'setEquityAction']),
		cardClick(index, url) {
			this.cardIndex = index;
			this.buyLink = url;
		},
		loca() {
			location.reload();
		},
		cardPayBtn() {
			console.log('调用支付===========');
			if (this.buyLink == null) {
				this.$toast('请选择要开通的会员卡');
				return;
			}
			try {
				let data = {
					url: this.buyLink
				};
				if (this.system == 'ios') {
					window.webkit.messageHandlers.redirectToYZ.postMessage(data);
				} else {
					window.android.playCourse('redirectToYZ', JSON.stringify(data));
				}
				this.cardIndex = -1;
			} catch (e) {
				//TODO handle the exception
			}
		},
		setRouter(val) {
			if(this.memberInfo.code == 0){
				this.$toast('请先开通会员');
				return;
			}
			this.$router.push({ name: val });
		},
		getVipAll() {
			console.log('调用接口');
			this.$axios
				.getVipContent()
				.then(res => {
					this.audioList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.getVipActivity()
				.then(res => {
					this.activityList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.getUserHelp()
				.then(res => {
					this.helpList = res.data.data.list;
				})
				.catch(err => {});
			this.$axios
				.getMemberCard()
				.then(res => {
					this.cardList = res.data.data.list;
				})
				.catch(err => {});
			this.isLoading = true;
		},
		musicDaile(id, name) {
			try {
				let data = {
					type: 1,
					id: id,
					name: name
				};
				window.webkit.messageHandlers.share.postMessage(data);
			} catch (e) {
				//TODO handle the exception
			}
		}
	},
	components: {},
	watch: {
		a(val, oldVal) {
			//普通的watch监听
			console.log('a:===========' + val, oldVal);
		}
	}
};
