export default {
	data() {
		return {
			isLoading: false
		};
	},
	created() {
		this.cardBtn();
	},
	methods: {
		cardBtn() {
			this.$axios.userApplyTime().then(res => {
				if (res.data.data == '该用户还未报名智慧早教课程') {
					this.isLoading = true;
				} else {
					this.$router.push({ name: 'education' });
				}
			});
		},
		userApply() {
			this.$axios
				.userApply()
				.then(res => {
					if (res.data.code == 1) {
						this.$toast('报名成功');
						this.$router.push({ name: 'education' });
						return true;
					}
				})
				.catch(err => {});
		}
	},
