export default {
	data() {
		return {
			isLoading: true
		};
	},
	created() {
		this.cardBtn();
	},
	methods: {
		cardBtn() {
			this.$axios
				.userApply()
				.then(res => {
					if (res.data.code == 1) {
						this.$toast('报名成功');
						this.$router.push({ name: "education" });
						return true;
					}else {
						this.$router.push({ name: "education" });
						return false;
					}
				})
				.catch(err => {});
		}
	},
	components: {}
};
