import Title from '@/components/Title.vue';
import Radar from '@/components/Radar.vue';
import { mapActions, mapMutations, mapState, mapGetters } from 'vuex';
export default {
	data() {
		return {
			title: ['宝宝成长值', '宝宝成长潜力'],
			age: '本月成长值',
			size: ['24', '17', , '15', '12', '10'],
			peportList: [],
			radarList1: [],
			radarList2: [],
			isLoading: false,
			time: [],
			sumDay: 0,
			today: new Date(),
			month:null,
		};
	},
	computed: {
		...mapState(['system', 'babyInfo', 'memberInfo'])
	},
	created() {
		this.month = this.today.getMonth() + 1;
		this.getUserReport();
		this.getSumTime();
	},
	methods: {
		getUserReport() {
			let radar = [];
			let radarVal = [{ value: [] }];
			this.$axios
				.getUserReport(this.month)
				.then(res => {
					this.peportList = res.data.data.list;
					let list = res.data.data.list;
					let radarObj = {};
					let radarArray = [];
					list.forEach(function(item, index) {
						radarObj.name = item.ability;
						radarObj.max = 100;
						radar.push(radarObj);
						radarArray.push(item.score);
					});
					radarVal[0].value = radarArray;
					this.radarList1 = radar;
					this.radarList2 = radarVal;
					this.isLoading = true;
				})
				.catch(err => {
					console.log(err);
				});
		},
		getSumTime() {
			this.$axios
				.sumTime(this.month)
				.then(res => {
					let list = [];
					this.sumDay = res.data.data.clockDays;
					this.time = res.data.data;
					let str = this.time.timeLength;
					let array = str.split('小时');
					list.push(array[0]);
					let m = array[1].split('分');
					list.push(m[0]);
					this.time = list;
				})
				.catch(err => {});
		}
	},
	components: {
		'v-title': Title,
		'v-radar': Radar
	}
};
